import React, {useState} from 'react';
import { Flashcard, FlashcardSet } from '../components/Flashcard.js';
import '../flashcard.css';

export function Flashcards() {

    // Example data for flashcards
    const flashcards = [
        { front: "日", back: "Sun, day, Japan" },
        { front: "月", back: "Moon, month" },
        { front: "火", back: "Fire" },
    ];

    const [currentIndex, setCurrentIndex] = useState(0);

    const nextCard = () => {
        setCurrentIndex((prevIndex) => (prevIndex + 1) % flashcards.length);
    };

    const prevCard = () => {
        setCurrentIndex((prevIndex) => prevIndex === 0 ? flashcards.length - 1 : prevIndex - 1);
    };

    const setFirstCard = () => {
        setCurrentIndex(0);
    }

    return (
        <div>
            <h1 className='page-title'>Flashcards</h1>
            <Flashcard 
                frontContent={flashcards[currentIndex].front}
                backContent={flashcards[currentIndex].back}
            />
            <div>
                <button onClick={prevCard}>Prev</button>
                <button onClick={nextCard}>Next</button>
                <button onClick={setFirstCard}>Reset to First</button>
            </div>
        </div>
    );
}
